INSERT INTO `w_cechypostaci` VALUES (0,'LUCKPOINTS','Punkty Szczęścia','PS','Ta cecha określa szczęście Bohatera.'),(1,'WEAPONSKILL','Walka Wręcz','WW','Ta cecha odzwierciedla sprawność Bohatera w walce, tak bronią białą, jak i bez broni.'),(2,'BALLISTICSKILL','Umięjętności Strzeleckie','US','Ta cecha odzwierciedla sprawność Bohatera w strzelaniu z łuku, kuszy lub broni palnej, a także w używaniu broni rzucanej.'),(3,'STRENGTH','Krzepa','K','Ta cecha odzwierciedla tężyznę fizyczną Bohatera.'),(4,'TOUGHNESS','Odporność','Odp','Ta cecha odzwierciedla odporność fizyczną Bohatera na efekty zranień, trucizn i różnych chorób.'),(5,'AGILITY','Zręczność','Zr','Ta cecha odzwierciedla sprawność fizyczną Bohatera, szybkość jego reakcji oraz sprawność manualną. '),(6,'INTELLIGENCE','Inteligencja','Int','Ta cecha odzwierciedla sprawność intelektualną Bohatera, jego zdolność rozumowania oraz zapamiętywania faktów.'),(7,'WILLPOWER','Siła Woli','SW','Ta cecha odzwierciedla umiejętność koncentracji Bohatera, jego determinacja oraz odporność na stres.'),(8,'FELLOWSHIP','Ogłada','Ogd','Ta cecha odzwierciedla charyzmę Bohatera, jego urok osobisty oraz obycie w kontaktach z innymi osobami.'),(9,'ATTACK','Atak','A','Ta cecha określa szybkość ataku Bohatera. Wartość cechy oznacza liczbę ataków, jakie może on wykonać w ciągu 10 sekund.'),(10,'WOUNDS','Żywotność','Żyw','Ta cecha określa ogólną witalność Bohatera. Wartość cechy oznacza liczbę obrażeń, jakie może otrzymać, nim jego życie stanie się zagrożone.'),(11,'STRENGTHBONUS','Siła','S','Ta cecha, wynikająca z cechy Krzepa, określa liczbę obrażeń, jakie Bohater zadaje w walce.'),(12,'TOUGHNESSBONUS','Wytrzymałość','Wt','Ta cecha, wynikająca z cechy Odporność, określa zdolność Bohatera do ignorowania części otrzymanych obrażeń.'),(13,'MOVEMENT','Szybkość','Sz','Ta cecha określa prędkość poruszania się Bohatera. Zasady szybkości poruszania się w trakcie walki: 1 Sz = 2 Ruch/Odwrót = 4 Szarża = 6 Bieg.\r\n'),(14,'MAGIC','Magia','Mag','Ta cecha określa potencjał magiczny Bohatera i jego zdolność rzucania zaklęć.'),(15,'INSANITYPOINTS','Punkty Obłędu','PO','Ta cecha określa stan zdrowia psychicznego Bohatera. BG zaczyna grę z liczbą PO równą 0, a ta wartość może wzrosnąć w czasie gry, w następstwie przerażających wydarzeń i ciężkich ran.'),(16,'FATEPOINTS','Punkty Przeznaczenia','PP','Ta cecha określa szczęście Bohatera i do pewnego stopnia jego przeznaczenie. Punkty Przeznaczenia mogą być wykorzystane, by uniknąć nieuchronnej śmierci lub okaleczenia.'),(17,'DAMAGE_WW','Obrażenia_WW','dWW',''),(18,'DAMAGE_US','Obrażenia_US','dUS',''),(19,'DAMAGE','Obrażenia','dmg',''),(20,'FATEINS','Przeznaczenie & Obłęd','PPO',''),(21,'LUCKMOTIVE','Szczęście & Motywacja','PSM',''),(22,'LUCKPOINTS','Punkty Szczęścia','PS','Ta cecha określa szczęście Bohatera.'),(23,'MOTIVATEPOINTS','Punkty Motywacji','PM','Ta cecha określa jak bardzo Bohater jest zmotywowany.');
